INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-213.3.6', '11B-213.3.6', 'Bathing facilities.', 'Where bathtubs or showers are provided, at least one bathtub complying with Section 11B-607 or at least one shower complying with Section 11B-608 shall be provided. Where two or more accessible showers are provided within the same functional area, at least one shower shall be opposite hand from the other or others (that is, one left-hand controls versus right-hand controls). Transfer type shower compartments shall be permitted in transient lodging guest rooms, multibedroom housing units in undergraduate student housing and residential dwelling units; and shall not be permitted at other locations to meet the requirements of Section 11B-213.3.6.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-213', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh02_Sec11B_213', 'subsection', 'ee0e228e9dc11942e46ca9344d422a2d39cf5d3bddf30702e4c59c11acbc91ee')
