INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-213.3.7', '11B-213.3.7', 'Coat hooks and shelves.', 'Where coat hooks or shelves are provided in toilet rooms without toilet compartments, at least one of each type shall comply with Section 11B-603.4. Where coat hooks or shelves are provided in toilet compartments, at least one of each type complying with Section 11B-604.8.3 shall be provided in toilet compartments required to comply with Section 11B-213.3.1. Where coat hooks or shelves are provided in bathing facilities, at least one of each type complying with Section 11B-603.4 shall serve fixtures required to comply with Section 11B-213.3.6.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-213', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh02_Sec11B_213', 'subsection', 'fcac8337ceb5b2d614e98c22b86845d482b5f42e629692bd6ea3ee65523530d6')
