INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-206.2.1', '11B-206.2.1', 'Site arrival points.', 'At least one accessible route shall be provided within the site from accessible parking spaces and accessible passenger drop-off and loading zones; public streets and sidewalks; and public transportation stops to the accessible building or facility entrance they serve. Where more than one route is provided, all routes must be accessible.

Exceptions:

1. Reserved.

2. An accessible route shall not be required between site arrival points and the building or facility entrance if the only means of access between them is a vehicular way not providing pedestrian access.

3. General circulation paths shall be permitted when located in close proximity to an accessible route.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-206', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh02_Sec11B_206', 'subsection', 'ccae31688906616cda6c01a25b74909c4545f397328141ff33d0f381bdc427fb')
