INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-206.2.4', '11B-206.2.4', 'Spaces and elements.', 'At least one accessible route shall connect accessible building or facility entrances with all accessible spaces and elements within the building or facility, including mezzanines, which are otherwise connected by a circulation path unless exempted by Section 11B-206.2.3 Exceptions 1 through 7.

