INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-404.2.7', '11B-404.2.7', 'Door and gate hardware', 'Handles, pulls, latches, locks and other operable parts on doors and gates shall comply with Section 11B-309.4. Operable parts of such hardware shall be 34 inches (864 mm) minimum and 44 inches (1118 mm) maximum above the finish floor or ground. Where sliding doors are in the fully open position, operating hardware shall be exposed and usable from both sides.

Exceptions:

1. Existing locks shall be permitted in any location at existing glazed doors without stiles, existing overhead rolling doors or grilles and similar existing doors or grilles that are designed with locks that are activated only at the top or bottom rail.

2. Access gates in barrier walls and fences protecting pools, spas and hot tubs shall be permitted to have operable parts of the release of latch on self-latching devices at 54 inches (1372 mm) maximum above the finish floor or ground provided the self-latching devices are not also self-locking devices and operated by means of a key, electronic opener or integral combination lock.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-404', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh04_Sec11B_404', 'subsection', 'd95d441c13de59802dc5868b92ae7bd92ecb40876a394e65b1ea88879940e8de')
