INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-208.3.1', '11B-208.3.1', 'General.', 'Parking spaces complying with Section 11B-502 that serve a particular building or facility shall be located on the shortest accessible route from parking to an entrance complying with Section 11B-206.4. Where parking serves more than one accessible entrance, parking spaces complying with Section 11B-502 shall be dispersed and located on the shortest accessible route to the accessible entrances. In parking facilities that do not serve a particular building or facility, parking spaces complying with Section 11B-502 shall be located on the shortest accessible route to an accessible pedestrian entrance of the parking facility.

Exceptions:

1. All van parking spaces shall be permitted to be grouped on one level within a multistory parking facility.

2. Parking spaces shall be permitted to be located in different parking facilities if substantially equivalent or greater accessibility is provided in terms of distance from an accessible entrance or entrances, parking fee and user convenience.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-208', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh02_Sec11B_208', 'subsection', '76ece9733cc578bd7ff1306a6d802f67e5f406462f29533c82474cabcee10efe')
