INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-208.3.2', '11B-208.3.2', 'Residential facilities.', 'In residential facilities containing residential dwelling units required to provide mobility features complying with Sections 11B-809.2 through 11B-809.4, and adaptable features complying with Sections 11B-809.6 through 11B-809.12, parking spaces provided in accordance with Section 11B-208.2.3.1 shall be located on the shortest accessible route to the residential dwelling unit entrance they serve. Spaces provided in accordance with Section 11B-208.2.3.2 shall be dispersed throughout all types of parking provided for the residential dwelling units.

Exception: Parking spaces provided in accordance with Section 11B-208.2.3.2 shall not be required to be dispersed throughout all types of parking if substantially equivalent or greater accessibility is provided in terms of distance from an accessible entrance, parking fee and user convenience.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-208', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh02_Sec11B_208', 'subsection', '417d176fc97a8d60de630daf25a334209fc72528efa4243f5e3d90f2405d6ce3')
