VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-809.10.1', '11B-809.10.1', 'General.', 'All bathrooms on an accessible route within residential dwelling units with adaptable features shall comply with Sections 11B-809.7,  11B-809.8, 11B-809.10.5.2, 11B-809.10.6.4, 11B-809.10.7.3 and 11B-809.12.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-809', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh08_Sec11B_809', 'subsection', '027261f71b6749f7d21336471ff5eef930d3caed19a10a72a9f2cfe709c4b660')
