INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-809.10.2', '11B-809.10.2', 'Number of complying bathrooms and fixtures.', 'One bathroom and one fixture of each type within the dwelling unit shall be designed to comply with the following:

1. Maneuvering space in bathrooms shall comply with Section 11B-809.10.4.

2. Bathtubs shall comply with Section 11B-809.10.5.

3. Showers shall comply with Section 11B-809.10.6.

4. Water closets shall comply with Section 11B-809.10.7.

5. Lavatories, vanities, mirrors and towel bars shall comply with Section 11B-809.10.8. When two or more lavatories are provided, at least one shall comply with Section 11B-809.10.8.

6. Where both a tub and shower are provided in the bathroom, at least one shall be made accessible. Where two or more bathrooms are provided, when a bathtub is installed in the first bathroom in compliance with Section 11B-809.10.5 and a shower compartment is provided in a subsequent bathroom, at least one shower compartment shall comply with Section 11B-809.10.6.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-809', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh08_Sec11B_809', 'subsection', '754aae3462ac2760b60e196aeb83b6ba08b2117e2484a76ab2a5f1b7476f6121')
