INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-809.10.4', '11B-809.10.4', 'Sufficient maneuvering space.', 'Where doors swing into the bathrooms or powder rooms required to comply with Section 11B-809.10, a clear maneuvering space of 30 inches (762 mm) by 48 inches (1219 mm) minimum shall be provided outside the arc of the door swing. The clear maneuvering space shall be permitted to include knee and toe clearance under bathroom fixtures. Doors shall be permitted to encroach into the clear floor space or clearance for fixtures where clear maneuvering space is provided outside the arc of the door swing. A turning space is not required within the room.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-809', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh08_Sec11B_809', 'subsection', '9e57ad6bf67ff8f2bca44ee6f01a950ecaec639136f126b54e1c8c1c7ed02f5a')
