INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-411.2.1.6', '11B-411.2.1.6', 'Identification of floors served.', 'In buildings with two or more elevator banks, each serving a different group of specific floors, hall call consoles located on floors with a building entry, including parking and transfer levels, shall be provided with signs complying with Sections 11B-703.2, 11B-703.3 and 11B-703.5 on the surface of or above the hall call console stating “FLOORS n1 – n2,” where n1 – n2 represents the range of floors served. Characters shall be white on a black background. When the accessibility function button is pressed, the audio output shall provide a verbal announcement of the floors served by the elevator group.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-411', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh05_Sec11B_411', 'subsection', 'edf06a4f162e2825e78a2c24db88ba43f944f89136e14779249fd2de4388c2ab')
