VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-707.7.2', '11B-707.7.2', 'Characters.', 'Characters displayed on the screen shall be in a sans serif font. Characters shall be 3/16 inch (4.8 mm) high minimum based on the uppercase letter “I”. Characters shall contrast with their background with either light characters on a dark background or dark characters on a light background.', 'ICC+CBC_Chapter11A_11B+2025+CA', 'accessibility', 'ICC:CBC_Chapter11A_11B:2025:CA:11B-707', 'https://codes.iccsafe.org/content/CABC2025P1/chapter-11b-accessibility-to-public-buildings-public-accommodations-commercial-buildings-and-public-housing#CABC2025P1_Ch11B_SubCh07_Sec11B_707', 'subsection', '8fb5c5ed015d428ce17c3920553c95b5d59f56487286d7655dc5328d4f5456fc')
