INSERT INTO sections (key, number, title, text, code_id, code_type, parent_key, source_url, item_type, hash)
VALUES ('ICC:CBC_Chapter11A_11B:2025:CA:11B-1008.2.5', '11B-1008.2.5', 'Ramps.', 'Within play areas, ramps connecting ground level play components and ramps connecting elevated play components shall comply with Section 11B-1008.2.5.

